import React, { useState } from "react";
import AQICard from "./components/AQICard";
import "./App.css";

export default function App() {
  const [city, setCity] = useState("");
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);
  const [err, setErr] = useState(null);

  async function search(e) {
    e && e.preventDefault();
    setErr(null);
    setResult(null);

    if (!city.trim()) return setErr("Please enter a valid city name");

    setLoading(true);
    try {
      const q = encodeURIComponent(city.trim());
      const res = await fetch(`http://localhost:4000/api/air?city=${q}`);
      const body = await res.json();

      if (!res.ok) {
        setErr(body.error || "Something went wrong");
      } else {
        setResult(body);
      }
    } catch {
      setErr("Backend server unreachable. Start your Node backend.");
    } finally {
      setLoading(false);
    }
  }

  return (
    <div className="app-wrapper">
      <div className="app-card">
        <h1 className="title">Air Quality Index</h1>
        <p className="subtitle">Search global AQI data instantly</p>

        <form onSubmit={search} className="search-bar">
          <input
            value={city}
            onChange={(e) => setCity(e.target.value)}
            placeholder="Search city (e.g. Delhi, London)"
          />
          <button type="submit" disabled={loading}>
            {loading ? "Searching..." : "Search"}
          </button>
        </form>

        {err && <div className="error">{err}</div>}
        {result && <AQICard payload={result} />}
      </div>

      <footer className="footer">
        <small>Ensure backend & AQICN token are configured properly.</small>
      </footer>
    </div>
  );
}
