import React from 'react';
import { Bar } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Tooltip,
  Legend
} from 'chart.js';

ChartJS.register(CategoryScale, LinearScale, BarElement, Tooltip, Legend);

function IAQIList({ iaqi }) {
  if(!iaqi) return <em>No components</em>;
  const keys = Object.keys(iaqi);
  return (
    <div className="iaqi-grid">
      {keys.map(k => (
        <div key={k} className="iaqi-item">
          <div style={{fontSize:12,fontWeight:600}}>{k.toUpperCase()}</div>
          <div style={{marginTop:6}}>{iaqi[k]}</div>
        </div>
      ))}
    </div>
  );
}

export default function AQICard({ payload }) {
  const src = payload.source;
  const data = payload.data;
  const iaqi = data.iaqi || {};

  const labels = Object.keys(iaqi);
  const values = labels.map(l => iaqi[l]);

  const chartData = {
    labels,
    datasets: [
      {
        label: 'Concentration (iaqi)',
        data: values,
        borderWidth: 1
      }
    ]
  };

  return (
    <div className="card" style={{marginTop:12}}>
      <div style={{display:'flex',justifyContent:'space-between',alignItems:'center'}}>
        <div>
          <div style={{fontSize:20,fontWeight:700}}>{data.city}</div>
          <div className="muted">source: {src}</div>
        </div>
        <div style={{textAlign:'center'}}>
          <div className="aqi-value">{data.aqi}</div>
          <div className="muted">AQI</div>
        </div>
      </div>

      <div style={{marginTop:12}}>
        <div><strong>Dominant pollutant:</strong> {data.dominentpol || 'N/A'}</div>
        <div><strong>Last updated:</strong> {data.time || 'N/A'}</div>
        <div style={{marginTop:8}}><strong>Coordinates:</strong> {data.geo ? `${data.geo[0]}, ${data.geo[1]}` : 'N/A'}</div>
      </div>

      <h4 style={{marginTop:14}}>Components</h4>
      <IAQIList iaqi={iaqi} />

      <div style={{marginTop:16}}>
        <Bar data={chartData} />
      </div>

      <details style={{marginTop:12}}>
        <summary>Raw data</summary>
        <pre style={{maxHeight:300,overflow:'auto',background:'#0b1220',color:'#dff6ea',padding:12,borderRadius:8}}>
{JSON.stringify(data.raw, null, 2)}
        </pre>
      </details>
    </div>
  );
}
