const getRiskLevel = (aqi) => {
  if (!aqi) return { label: "Unknown", percent: 0, color: "#888" };

  if (aqi <= 50)
    return { label: "Good", percent: 20, color: "var(--good)" };

  if (aqi <= 100)
    return { label: "Moderate", percent: 40, color: "var(--moderate)" };

  if (aqi <= 150)
    return { label: "Unhealthy (SG)", percent: 60, color: "var(--usg)" };

  if (aqi <= 200)
    return { label: "Unhealthy", percent: 75, color: "var(--unhealthy)" };

  if (aqi <= 300)
    return { label: "Very Unhealthy", percent: 90, color: "var(--very-unhealthy)" };

  return { label: "Hazardous", percent: 100, color: "var(--hazardous)" };
};


