import React from "react";
import { Line } from "react-chartjs-2";
import {
  Chart as ChartJS,
  LineElement,
  PointElement,
  LinearScale,
  CategoryScale,
  Legend,
  Tooltip,
} from "chart.js";

ChartJS.register(LineElement, PointElement, LinearScale, CategoryScale, Legend, Tooltip);

export default function AQICard({ payload }) {
  if (!payload || !payload.data) return null;

  const data = payload.data;

  const pm25Forecast = data.forecast?.pm25 || [];
  const pm10Forecast = data.forecast?.pm10 || [];

  const chartData = {
    labels: pm25Forecast.map((d) => d.day),
    datasets: [
      {
        label: "PM2.5 Forecast",
        data: pm25Forecast.map((d) => d.avg),
        borderWidth: 2,
        borderColor: "red",
        tension: 0.2,
      },
      {
        label: "PM10 Forecast",
        data: pm10Forecast.map((d) => d.avg),
        borderWidth: 2,
        borderColor: "blue",
        tension: 0.2,
      },
    ],
  };

  return (
    <div className="aq-card">
      <h2>{data.city}</h2>
      <h3>AQI: {data.aqi}</h3>
      <p><b>Dominant Pollutant:</b> {data.dominant}</p>

      <div className="section">
        <h3>Live Pollutants</h3>
        <ul>
          {Object.entries(data.iaqi || {}).map(([k, v]) => (
            <li key={k}>
              {k.toUpperCase()}: {v?.v}
            </li>
          ))}
        </ul>
      </div>

      <div className="section">
        <h3>Forecast</h3>
        <Line data={chartData} />
      </div>

      <div className="section">
        <h3>Coordinates</h3>
        <p>
          Lat: {data.geo?.[0]} <br />
          Lon: {data.geo?.[1]}
        </p>
      </div>

      <div className="section">
        <h3>Updated Time</h3>
        <p>{data.time?.s}</p>
      </div>

      <div className="section">
        <h3>Sources</h3>
        <ul>
          {data.attributions?.map((a, idx) => (
            <li key={idx}>
              <a href={a.url} target="_blank" rel="noreferrer">
                {a.name}
              </a>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}
