import React from "react";
import "./AQICard.css";

export default function AQICard({ payload }) {
  // payload = { source, data }
  const data = payload.data || {};

  const city = data.city;
  const aqi = data.aqi;
  const dominentpol = data.dominant || data.dominentpol;
  const time = data.time;
  const iaqi = data.iaqi;

  function getAQIColor(value) {
    if (value <= 50) return "good";
    if (value <= 100) return "moderate";
    if (value <= 150) return "sensitive";
    if (value <= 200) return "unhealthy";
    if (value <= 300) return "very-unhealthy";
    return "hazardous";
  }

  return (
    <div className={`aqi-card ${getAQIColor(aqi)}`}>
      <h2>{city}</h2>

      <div className="aqi-value">{aqi ?? "N/A"}</div>

      <p className="dominent">
        Dominant pollutant: {dominentpol || "N/A"}
      </p>

      <p className="updated">
        Last updated: {time?.s || "N/A"}
      </p>

      <div className="details-grid">
        {iaqi
          ? Object.entries(iaqi).map(([key, val]) => (
              <div key={key} className="detail-box">
                <span className="label">{key.toUpperCase()}</span>
                <span className="value">{val?.v ?? "N/A"}</span>
              </div>
            ))
          : <div>No IAQI data</div>
        }
      </div>
    </div>
  );
}
