import React, { useState } from 'react';
import AQICard from './components/AQICard';

export default function App() {
  const [city, setCity] = useState('');
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);
  const [err, setErr] = useState(null);

  async function search(e) {
    e && e.preventDefault();
    setErr(null);
    setResult(null);
    if(!city || city.trim() === '') return setErr('Enter city name');
    setLoading(true);
    try {
      const q = encodeURIComponent(city.trim());
      const res = await fetch(`http://localhost:4000/api/air?city=${q}`);
      const body = await res.json();
      if(!res.ok) {
        setErr(body.error || JSON.stringify(body));
      } else {
        setResult(body);
      }
    } catch(er) {
      setErr('Could not contact backend. Make sure server is running.');
    } finally {
      setLoading(false);
    }
  }

  return (
    <div className="container">
      <header>
        <h1>AQI Search (MERN)</h1>
        <p>Search city and get AQI. Data provided by AQICN</p>
      </header>

      <form onSubmit={search} className="search">
        <input value={city} onChange={e=>setCity(e.target.value)} placeholder="eg. Delhi, New York" />
        <button type="submit" disabled={loading}>Search</button>
      </form>

      {loading && <div className="muted">Loadingâ€¦</div>}
      {err && <div className="error">{err}</div>}
      {result && <AQICard payload={result} />}

      <footer>
        <small>Make sure backend is running and AQICN token is set in backend/.env</small>
      </footer>
    </div>
  );
}
